#define PAGE_CONTENT_INDEX "<html><head><meta charset=UTF-8><title>Train Config App</title><meta name=viewport content='width=device-width, initial-scale=1.0'><style>html,body{background:#eee;color:#222;line-height:1.2;font-family:'Segoe UI','Roboto','Tahoma','Geneva','Verdana',sans-serif;padding:0;margin:0}.container{display:flex;flex-wrap:wrap}h1{margin:0;margin-top:5px;margin-bottom:5px}nav{background:#333;height:48px;flex:1 100%}nav>h1{margin:0;padding:10px;color:#eee;display:inline-block}nav>a{float:right;line-height:48px;background:#404040;color:#eee;text-decoration:none;padding:0 10px}nav>a:hover{background:#5e5e5e}button{font-size:1.2em;margin-top:10px;background:#077e11;color:#fff;border:0;padding:10px;border-radius:5px;box-shadow:0 .3px .9px rgba(0,0,0,0.108),0 1.6px 3.6px rgba(0,0,0,0.132);transition:.2s ease background,0.2s ease transform;outline:none !important;position:relative}button:hover{transform:translateY(-2px);box-shadow:0 2px 20px 0 rgba(114,196,89,0.432)}button:active{background:#07530e;transform:translateY(1px);box-shadow:inset 2px 2px 10px 0 rgba(25,26,25,0.253)}.whiteBlock{background:#fefefe;box-shadow:0 .3px .9px rgba(0,0,0,0.108),0 1.6px 3.6px rgba(0,0,0,0.132)}section{padding:10px;border-radius:3px;margin:10px;flex-grow:.3}label{position:relative;display:block;margin-top:10px;margin-bottom:5px}label>input{display:block;border-radius:4px;border:1px solid #8d8d8d;padding:5px;font-size:1em;padding-top:15px;outline:0}label>input:focus~span.desc,label>input:not(:focus):valid~span.desc{background:#fff;top:-6px;left:10px;font-size:11px;opacity:1}label>span{color:#333;position:absolute;pointer-events:none;left:10px;top:25%;transition:.2s ease all;z-index:999}table,tr,td{border-collapse:collapse;border:1px solid #444}td{padding:4px}tr>td:nth-of-type(1){background:#333;color:#eee;text-align:right;font-weight:600}meter{width:175%}@media screen and (max-width:768px){section{width:100%}}</style></head><body><div class=container><nav><h1>Super Clever Train</h1><a href=/update>UPDATE</a></nav><section class=whiteBlock><h1>Config</h1><form action=/save id=mainform>Loading...</form></section><section class=whiteBlock><h1>Status</h1><table id=maintable><tr><td>Loading...</td></tr></table></section><section class=whiteBlock><h1>Calibration</h1><div id=thresholdBox></div><form action=/save><label><input name=analog_threshold id=analog_thresholdInput required><span class=desc>Threshold</span></label><input name=reloadNetwork value=false hidden><button>Save</button></form></section></div><script>function update(full){fetch('./status').then(function(response) {  return response.json();}).then(function(myJson) {var tbl = document.getElementById('maintable');var frm = document.getElementById('mainform');tblJson = myJson.table;formJson = myJson.form;keys = Object.keys(tblJson);tbl.innerHTML = '';for (i = 0; i < keys.length; i++){key = keys[i];value = tblJson[key];row = tbl.insertRow(-1);cell = row.insertCell(-1);cell.innerHTML = key;cell = row.insertCell(-1);cell.innerHTML = value;}c = '';if (parseInt(tblJson.analog_value) > parseInt(tblJson.analog_threshold)){c = 'filter: saturate(0);';}tbl.innerHTML += `<meter min=0 max=1024 value=${tblJson.analog_value} style='${c}'></meter>`;if (full){document.getElementById('analog_thresholdInput').value = tblJson.analog_threshold;keys = Object.keys(formJson);frm.innerHTML = '';for (i = 0; i < keys.length; i++){key = keys[i];value = formJson[key];frm.innerHTML += `<label><input name='${key}' value='${value}' required><span class='desc'>${key.replace('_', ' ')}</span></label>`;}frm.innerHTML += `<button>Save</button>`;}});}update(true);setInterval(function(){update(false)}, 400);</script></body></html>"
#define PAGE_CONTENT_SAVED "<style>html, body{background: #eee;color: #222;font-family: 'Segoe UI', 'Roboto', 'Tahoma', 'Geneva', 'Verdana', sans-serif;padding: 5%;padding-top: 1vh;font-size: 2em;}h1, h3{margin: 0;}h1{color: rgb(20, 143, 16);text-shadow: 2px 2px 3px rgba(0, 0, 0, 0.5);}h3{color: #555;font-size: 0.7em;text-shadow: 2px 2px 3px rgba(255,255,255,0.5);}h3>a{color: rgb(37, 64, 216);text-decoration: none;}</style><h1>OK!</h1><h3>Settings saved succesfully</h3><h3>If you are not redirected <a href=/ >click here</a></h3><script>setTimeout(function(){document.location.href='/'}, 200);</script>"
#define PAGE_CONTENT_404 "<style>html, body{background: #eee;color: #222;font-family: 'Segoe UI', 'Roboto', 'Tahoma', 'Geneva', 'Verdana', sans-serif;padding: 5%;padding-top: 1vh;font-size: 2em;}h1, h3{margin: 0;}h1{color: rgb(160, 132, 132);text-shadow: 2px 2px 3px rgba(255,255,255,0.5);}h3{color: #555;font-size: 0.7em;text-shadow: 2px 2px 3px rgba(255,255,255,0.5);}</style><h1>404</h1><h3>Page not found</h3>"